"use strict";

module.exports = function (t, a) {
	a(t.call(new Date(2001, 0, 1)), 31, "Enero");
	a(t.call(new Date(2001, 1, 1)), 28, "Febrero");
	a(t.call(new Date(2000, 1, 1)), 29, "Febrero (leap)");
	a(t.call(new Date(2001, 2, 1)), 31, "Marzo");
	a(t.call(new Date(2001, 3, 1)), 30, "Abril");
	a(t.call(new Date(2001, 4, 1)), 31, "Mayo");
	a(t.call(new Date(2001, 5, 1)), 30, "Junio");
	a(t.call(new Date(2001, 6, 1)), 31, "Julio");
	a(t.call(new Date(2001, 7, 1)), 31, "Agosto");
	a(t.call(new Date(2001, 8, 1)), 30, "Septiembre");
	a(t.call(new Date(2001, 9, 1)), 31, "Octubre");
	a(t.call(new Date(2001, 10, 1)), 30, "Noviembre");
	a(t.call(new Date(2001, 11, 1)), 31, "Diciembre");
};
